            elif three_rate >= 5: msg = f'3봉 상승 5% 이상 ({three_rate:.2f}%) 7이평 이탈 매도'
            elif rise_rate >= 7: msg = f'상승 시작 후 7% 이상 ({rise_rate:.2f}%) 7이평 이탈 매도'
            elif blue(1): msg = f'7 이평 이탈 매도' # size >= 2.0 
